function onScreen(thing){$(thing).each(function(){var win_scroll=window.scrollY||document.documentElement.scrollTop,window_height=window.innerHeight,element=$(this),element_height=element.outerHeight(),element_offset=element.offset(),position_top=element_offset.top;win_scroll>position_top-window_height&&win_scroll<position_top+element_height?$(this).addClass("on"):$(this).removeClass("on")})}$(document).ready(function(){onScreen(".on-screen")}),$(window).resize(function(){onScreen(".on-screen")}),window.addEventListener("scroll",function(){onScreen(".on-screen")});